import * as React from 'react';
import { Box, Button, InputLabel, MenuItem, Paper, Select } from "@mui/material"
import axios from "axios"
import style from './UploadArquivo.module.scss'

export const UploadArquivo = ({ user, veiculo }) => {

    // const [imagens1, setImagens1] = React.useState([])
    // const [imagens2, setImagens2] = React.useState([])
    // const [imagensURLs1, setImagensURLs1] = React.useState([])
    // const [imagensURLs2, setImagensURLs2] = React.useState([])

    const [ imagem, setImagem ] = React.useState({
        rg: File,
        comprovante_endereco: File
    })



    // React.useEffect(() => {
    //     if (imagens1.length < 1) return;
    //     const novaImagensURLs1 = [];
    //     const novaImagensURLs2 = [];
    //     imagens1.forEach(imagem => novaImagensURLs1.push(URL.createObjectURL(imagem)));
    //     setImagensURLs1(novaImagensURLs1);
    //     if (imagens2.length < 1) return;
    //     imagens2.forEach(imagem => novaImagensURLs2.push(URL.createObjectURL(imagem)));
    //     setImagensURLs2(novaImagensURLs2);
    // }, [imagens1, imagens2])

    // const onImagens = (e) => {
    //     setImagem(a => ({...a, }))
    //     console.log(imagem)
    //     // console.log(e.target.files)
    //     // console.log(e.target.name)
    //     // e.target.name === 'rg' && setImagens1([...e.currentTarget.files])
    //     // e.target.name === 'comprovante_endereco' && setImagens2([...e.currentTarget.files])
    // }

    const [retorno, setRetorno] = React.useState()
    const [grt, setGrt] = React.useState("")

    const escolheGrt = (e) => {
        setGrt(e.target.value)
    }

    React.useEffect(() => {
        axios.get("http://localhost:3001/GRT/Utilizaveis", {
            params: { user }
        })
            .then(res => {
                console.log(res.data)
                setRetorno(res.data)
            })
            .catch(err => console.log(err))
    }, [])


    const salvarGrt = (e) => {
        e.preventDefault()
        // axios.post('http://localhost:3001/Servico/GravaGrt', {user, veiculo, grt, })
        // .then(res => {
        //     console.log(res)
        // })
        // .catch(err => console.log(err))
    }

    // console.log(user, retorno, grt, veiculo)
console.log(imagem)
    return (
        <>
            <Paper sx={{ p: 2 }}>
                <Box sx={{ width: '100%' }}>
                    <form className={style.container} onSubmit={salvarGrt}>
                        <div className={style.botaoUp}>
                            <InputLabel id="selecionaGrt">Selecione a GRT</InputLabel>
                            <Select
                                labelId="selecionaGrt"
                                id="SelGRT"
                                value={grt}
                                label='Selecione a GRT'
                                onChange={escolheGrt}
                            >
                                {/* <MenuItem value={10}>dez</MenuItem>
                                <MenuItem value={20}>vinte</MenuItem> */}
                                {retorno !== undefined ? retorno.map((iten) => {
                                    return (
                                        <MenuItem key={iten.id} value={iten.GRT}>{iten.GRT}</MenuItem>
                                    )
                                }) : null}
                            </Select>
                        </div>
                        <div className={style.boxConteiner}>
                            <Button id='rg' sx={{ m: 2 }} variant="contained" component="label">
                                Enviar rg
                                <input hidden accept="image/*" type="file" name='rg' onChange={(e) => setImagem(a => ({...a, rg: e.target.files}))} />
                            </Button>
                            <div className={style.amostra}>
                                {/* {imagens1.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs1.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)} */}
                                {imagem.rg.length !== 2 ? <p>teste</p> : <img src={imagem.rg} alt=''/>}
                            </div>
                        </div>
                        <div className={style.boxConteiner}>
                            <Button id='rg' sx={{ m: 2 }} variant="contained" component="label">
                                Enviar rg
                                <input hidden accept="image/*" type="file" name='comprovante_endereco' onChange={(e) => setImagem(a => ({...a, comprovante_endereco: e.target.files}))} />
                            </Button>
                            <div className={style.amostra}>
                                {/* {imagens2.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs2.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)} */}
                            </div>
                        </div>

                        <div className={style.botao}>
                            <Button color='success' type="submit">Salvar</Button>
                        </div>

                    </form>
                </Box>
            </Paper>
        </>
    )
}
