import React from 'react';
import { ApesquisaRealizada, Modal } from '../../api/alerta/alertas';

const AIProp = () => {

    const [dado, setDado] = React.useState();
    const [opt, setOpt] = React.useState();

    const EnvioDados = (e) => {
        e.preventDefault()
        console.log('enviado', dado, opt)
        Modal()
    }
    const CHASSI = () => {
        return (
            <>
                <label> </label>
                <input onChange={(e) => setDado(e.target.value)} placeholder='Digite o chassi aqui!' type='text' />
                <button type='onsubmit'>enviar</button>
            </>
        )
    }
    const PLACA = () => {
        return (
            <>
                <label>teste: </label>
                <input onChange={(e) => setDado(e.target.value)} placeholder='teste' type='text' />
                <button type='onsubmit'>enviar</button>
            </>
        )
    }
    console.log(dado,'teste' ,opt)
    return (
        <div className='tela--geral'>
            <div className='tela--consulta'>
                <h1>Alteração de Informações do Proprietário</h1>
                <form onSubmit={EnvioDados}>
                    <fieldset>
                        <label>PLACA: </label>
                        <input name='doc' id='placa' onChange={(e) => setOpt(e.target.id)} value='placa' type='radio'></input>
                        <label>CHASSI:</label>
                        <input name='doc' id='chassi' onChange={(e) => setOpt(e.target.id)} value='chassi' type='radio'></input>
                    </fieldset>
                    {opt === 'chassi' ? <CHASSI /> : null}
                    {opt === 'placa' ? <PLACA /> : null}
                </form>
            </div>
            <ApesquisaRealizada />
        </div>
    )
}
export default AIProp
