import React from 'react';
import { Button, TextField } from '@mui/material';
import { Cabecalho } from '../../api/cabecalho/Cabecalho';
import { Rodape } from '../../api/rodape/Rodape'
import style from './Inicial.module.scss'

const axios = require('axios').default;

const Inicial = () => {

    const dt = new Date().toLocaleString()

    function loginRealizado(a) {
        if (a === "try") {
            document.getElementById('login--modal').showModal();
            document.getElementById('msg').innerHTML = "Verificando usuário e senha!"
        } else if (a === "successo") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = `Login realizado com sucesso! ${dt}`
            }, 3000)
        } else if (a === "incorreto") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Usuário ou senha incorreto."
            }, 3000)
        } else if (a === "erro") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Houve um erro ao tentar logar no servidor, tente novamente em alguns minutos."
            }, 3000)
        }
    }
    function fechaDiag() {
        let b = document.getElementById('msg').innerHTML
        if (b !== "Verificando usuário e senha!") {
            document.getElementById('login--modal').close();
        }
    }

    const LoginRealiza = () => {
        return (
            <>
                <dialog id='login--modal' onClick={fechaDiag}>
                    <div>
                        <p id='msg'></p>
                    </div>
                </dialog>
            </>
        )
    }

    const [login, setLogin] = React.useState();
    const [senha, setSenha] = React.useState();

    const Login = (e) => {
        e.preventDefault()
        loginRealizado("try")
        axios.get('http://localhost:3005/Logar', {
            params: {
                login: login,
                senha: senha
            }
        })
        .then((response) => {
            if (response.status === 200) {
                if (!response.data) {
                    loginRealizado("incorreto")
                } else {
                    const {_id} = response.data 
                    console.log(_id)
                    loginRealizado("successo")
                    setTimeout(() => {
                        console.log('teste')
                        window.location.href = "Dashboard";
                    }, 5000)
                    axios.post(`http://localhost:3005/DataLogin`, {
                    })
                }
            }
        })
        .catch((err) => {
            console.log(err);
            loginRealizado("erro")
        })
        .finally(() => {
            document.getElementById('login--form').reset()
        })
    }

    return (
        <>
            <Cabecalho />
            <div className={style['box--central']}> 
                <div className={style.login}>
                    <h1>Login</h1>
                    <form className={style['login--form']} onSubmit={Login}>
                        <div className={style['login-user']}>
                            <TextField
                                id='login'
                                label='Usuário'
                                onChange={(e) => setLogin(e.target.value)}
                                color='success'
                                size='small'
                                fullWidth required />
                        </div>
                        <div className={style['login-user']}>
                            <TextField
                                id='senha'
                                label='Senha'
                                type='password'
                                onChange={(e) => setSenha(e.target.value)}
                                color='success'

                                size='small'
                                fullWidth required />
                        </div>
                        <div className={style['login-user']}>
                            <Button
                                variant='contained'
                                type='onSubmit'
                                color='success'
                                fullWidth>
                                Logar
                            </Button>
                        </div>
                        <p>Esqueci minha senha</p>
                    </form>
                    <LoginRealiza />
                </div>
                <div className={style['box--container']}>
                    <h1>Confira os serviços disponíveis:</h1>
                    <div>
                        <div>
                            <h2>Alterações de Documento</h2>
                            <li>Alteração de Informações do Proprietário</li>
                            <li>Alteração de Informações de Veículos</li>
                            <li>Alteração de Restrição Financeira</li>
                        </div>
                        <div>
                            <h2>Autorização</h2>
                            <li>Autorização para Alteração de Caracteristicas</li>
                            <li>Autorização para Fabricação de Placa</li>
                            <li>Autorização para Regravação de Chassi</li>
