import { Alert, Button, Checkbox, FormControl, FormControlLabel, Input, InputAdornment, Paper, Snackbar, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";
import style from './CadastroCliente.module.scss';
import moment from "moment";
import axios from "axios";

export const CadastroCliente = ({ veiculo, setCadastroCliente }) => {
    // console.log(veiculo)

    const [cadastro, setCadastro] = useState({
        nome: '',
        rgOuCnpj: '',
        endereco: '',
        utilizaEndereco: false,
        celular: '',
        email: '',
        restricaoFinanceira: false,
        agenteFinanceiro: '',
        alteraEndereco: false,
        novoEndereco: '',
        novoenderecoCEP: '',
        alteraInfoProprietario: false,
        alteraInfoVeiculo: false,
        alteraInfoFinanceira: false,
        alteraInfoEnderecoEntrega: false,
        novoEnderecoLogra: '',
        novoEnderecoNum: '',
        novoEnderecoBairro: '',
        novoEnderecoCidade: '',
        novoEnderecoUF: '',
        novoEnderecoCEP: '',
        correcaoRestricoes: false,
        emissaoDCPPO: false,
        fornecimentoPlacaExperienciaFabricante: false,
        inclusaoRestricaoFinanceria: false,
        liberacaoAverbacaoExecucao: false,
        liberacaoRestricaoFinanceira: false,
        licencaEspecialTransito: false,
        mudancaPlacaUnica: false,
        primeiroEmplacamento: false,
        renovacaoPlacaExperienciaFabricante: false,
        reservaPlaca: false,
        restricaoTransferencia: false,
        solicitacaoAlteracaoCaracteristicas: false,
        solicitacaoRegravacaoChassi: false,
        solicitacaoTransporteEscolar: false,
        alteracaoExecucao: false,
        baixaPlacaExperienciaFabricante: false,
        baixaOutraUF: false,
        baixaVeiculo: false,
        baixaVeiculoCampo: '',
        cancelamentoProcesso: false,
        colocacaoLacrePlaca: false,
        comunicacaoVenda: false,
        correcaoObservacoes: false,
        correcaoChassi: false,
        correcaoInformacoesProprietario: false,
        correcaoInformacoesVeiculo: false,
        correcaoMunicipio: false,
        autorizoEmailTelefone: false,
        naoAutorizoInformacoes: false,
        solicitaCertidao: false,
        solicitaCopiaDocumento: false,
        solicitaCopiaCRLV: false,
        solicitaVistoria: false,
        solicitaDCPPO: false,
        transferePropriedadeVeiculoOutro: false,
        transferePropriedadeVeiculoRS: false,
        trocaMunicipioOutroEstado: false,
        trocaMunicipioRS: false,
        Outros: false,
        campoOutros: '',
        relativoVeiculo: veiculo.data.modelo,
        relativoChassiOuPlaca: veiculo.data.chassi,
        data: moment().format("YYYY-MM-DD"),
    })

    const [segundaVia, setSegundaVia] = useState({
        CRLV: false,
        CRV_CRLV: false,
        segundaViaMotivo: ''
    })

    const [email1, setEmail1] = useState(false)

    const CadastraCelular = (e) => {
        const numero = e.target.value.replace(/[^0-9]/g, '');
        // console.log(numero)
        setCadastro(a => ({ ...a, celular: numero }))
    }

    const CadastroEmail = (e) => {
        let arroba = cadastro.email.includes("@")
        let ponto = cadastro.email.includes('.')
        if ((arroba === false) || (ponto === false)) {
            setEmail1(true)
        } else {
            setEmail1(false)
        }
    }

    const registroFinanceiro = (e) => {
        setCadastro(a => ({ ...a, restricaoFinanceira: false, agenteFinanceiro: '' }))
    }

    const SegundaVia = (e) => {
        const valida = e.target.id;
        // console.log(e.target.id)
        // console.log(e.target.checked)
        valida === 'CRLV' && setSegundaVia(a => ({ ...a, CRLV: e.target.checked, CRV_CRLV: false, segundaViaMotivo: '' }))
        valida === 'CRV_CRLV' && setSegundaVia(a => ({ ...a, CRLV: false, CRV_CRLV: e.target.checked, segundaViaMotivo: '' }))
    }

    const CadastroCliente = (e) => {
        e.preventDefault()
        console.log("Tentativa de cadastro")
        console.log(cadastro)
        axios.post('http://104.248.40.57:3001/Servicos/CadastrarCliente', {
            cadastro
        })
            .then(res => {
                setCadastroCliente(true)
                avisoPositivo()
                // console.log('Cliente cadastrado', res)
            })
            .catch(err => {
                avisoNegativo()
                // console.log('Cliente não cadastrado', err)
            })
    }

    const [ op, setOp ] = useState({
        op: false,
        tipo: 'info',
        frase: ''
    })
    const fecharAviso = () => { setOp(a => ({...a, op: false})) }
    const avisoPositivo = () => {
        setOp(a => ({...a,
