                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.naoAutorizoInformacoes}
                                            onChange={(e) => setCadastro(a => ({ ...a, naoAutorizoInformacoes: e.target.checked }))}
                                        />}
                                    label='Não autorizo o recebimento de informações do DETRAN/RS e'
                                />
                                <h2 className={style.nocheck}>estou ciente de que será minha responsabilidade obter</h2>
                                <h2 className={style.nocheck}>as informações relevantes ao meu veículo</h2>
                            </div>
                            <div className={style.boxrigth}>
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.solicitaCertidao}
                                            onChange={(e) => setCadastro(a => ({ ...a, solicitaCertidao: e.target.checked }))}
                                        />}
                                    label='Solicitação de Certidão'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.solicitaCopiaDocumento}
                                            onChange={(e) => setCadastro(a => ({ ...a, solicitaCopiaDocumento: e.target.checked }))}
                                        />}
                                    label='Solicitação de Cópia de Documentos'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.solicitaCopiaCRLV}
                                            onChange={(e) => setCadastro(a => ({ ...a, solicitaCopiaCRLV: e.target.checked }))}
                                        />}
                                    label='Solicitação de Cópia do CRLV'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.solicitaVistoria}
                                            onChange={(e) => setCadastro(a => ({ ...a, solicitaVistoria: e.target.checked }))}
                                        />}
                                    label='Solicitação de Vistoria'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.solicitaDCPPO}
                                            onChange={(e) => setCadastro(a => ({ ...a, solicitaDCPPO: e.target.checked }))}
                                        />}
                                    label='Solicitação de DCPPO'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.transferePropriedadeVeiculoOutro}
                                            onChange={(e) => setCadastro(a => ({ ...a, transferePropriedadeVeiculoOutro: e.target.checked }))}
                                        />}
                                    label='Transferência de Propriedade de Veículo de Outro Estado'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.transferePropriedadeVeiculoRS}
                                            onChange={(e) => setCadastro(a => ({ ...a, transferePropriedadeVeiculoRS: e.target.checked }))}
                                        />}
                                    label='Transferência de Propriedade de Veículo do RS'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.trocaMunicipioOutroEstado}
                                            onChange={(e) => setCadastro(a => ({ ...a, trocaMunicipioOutroEstado: e.target.checked }))}
                                        />}
                                    label='Troca de Município de Outro Estado'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.trocaMunicipioRS}
                                            onChange={(e) => setCadastro(a => ({ ...a, trocaMunicipioRS: e.target.checked }))}
                                        />}
                                    label='Troca de Município do RS'
                                />
                                <FormControlLabel
                                    sx={{ height: 22, whiteSpace: "nowrap" }}
                                    control={
                                        <Checkbox
                                            size='small'
                                            checked={cadastro.Outros}
                                            onChange={(e) => setCadastro(a => ({ ...a, Outros: e.target.checked, campoOutros: "" }))}
                                        />}
                                    label='Outros:'
                                />
                                <TextField
                                    disabled={!cadastro.Outros}
                                    variant='outlined'
                                    multiline
                                    rows={4}
                                    sx={{ m: '0 40px 0 0' }}
                                    value={cadastro.campoOutros}
                                    onChange={(e) => setCadastro(a => ({ ...a, campoOutros: e.target.value }))}
                                />
                            </div>
                        </div>
                        <div className={style.boxlinha}>
                            <h2>Relativo ao Veículo:</h2>
                            <FormControl fullWidth variant="standard">
                                <Input
                                    id="standard-adornment-amount"
                                    value={cadastro.relativoVeiculo}
                                    onChange={(e) => setCadastro(a => ({ ...a, relativoVeiculo: e.target.value }))}
                                    startAdornment={<InputAdornment position="start"></InputAdornment>}
                                />
                            </FormControl>
                            <h2>Chassi / Placa:</h2>
                            <FormControl sx={{ width: '40%' }} variant="standard">
                                <Input
                                    id="standard-adornment-amount"
                                    value={cadastro.relativoChassiOuPlaca}
                                    onChange={(e) => setCadastro(a => ({ ...a, relativoChassiOuPlaca: e.target.value }))}
                                    startAdornment={<InputAdornment position="start"></InputAdornment>}
                                />
                            </FormControl>
                        </div>
                        <h2 className={style.linhaservico}> </h2>
                        <div className={style.centrador}>
                            <input className={style.data} type='date' value={cadastro.data} onChange={e => setCadastro(a => ({ ...a, data: e.target.value }))} />
                            <div className={style.boxlinha}>
                                <Button sx={{ mt: 5 }} color='success' type='submit' >Cadastrar</Button>
                            </div>
                        </div>
                    </Paper>
                </Box>
            </form>
        </>
    )
}
