import React from "react";
import { Backdrop, Button, CircularProgress, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TableCell, TableRow } from "@mui/material"
import axios from "axios";



export const Linha = ({ row, labelId }) => {

    const [open, setOpen] = React.useState(false);
    const [open1, setOpen1] = React.useState(false);
    const [open2, setOpen2] = React.useState(false);
    const [response, setResponse] = React.useState();

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };
    const handleClose1 = () => {
        setOpen1(false);
    };

    // const handleConfirm = () => {
    //     console.log("clicado")

    //     axios.post('http://localhost:3001/GerarGRT', {
    //         row
    //     })
    //         .then(response => {
    //             console.log(response)
    //             setResponse(response.data)
    //             setOpen2(false)
    //             setOpen1(true)
    //         })
    //         .catch(error => {
    //             console.log(error)

    //         })
    //         .finally(final => {
    //             console.log(final)
    //             setOpen2(false)
    //         })
    // }

    const Dialogo1 = () => {
        return (
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="Validar"
                aria-describedby="Validar-description"
            >
                <DialogTitle id="Validar">
                    {row.quantidade > 1 ? "Validar as GRTs" : "Validar a GRT"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Validar-description">
                        {row.quantidade > 1 ? `Será liberado ${row.quantidade} GRTs ao despachante ${row.nome}` : `Será liberado 1 GRT ao despachante ${row.nome}`}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    {/* <Button color='success' onClick={() => handleConfirm()} >Confirmar</Button> */}
                    <Button color='error' onClick={handleClose} >Fechar</Button>
                </DialogActions>
            </Dialog>
        )
    }
    const Dialogo2 = () => {
        return (
            <Dialog
                open={open1}
                onClose={handleClose1}
                aria-labelledby="Confirmar"
                aria-describedby="Confirmar-description"
            >
                <DialogTitle id="Confirmar">
                    {row.quantidade > 1 ? "GRT inserida com sucesso" : "GRTs inseridas com sucesso"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Confirmar-description">
                        {row.quantidade > 1 ? `Foi liberado ${row.quantidade} GRTs ao despachante ${row.nome}` : `Será liberado 1 GRT ao despachante ${row.nome} sendo ela a ${response.GRTletra}${response.GRTnumero}`}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    {/* <Button color='success' onClick={handleConfirm} >Confirmar</Button> */}
                    <Button color='error' onClick={handleClose1} >Cancelar</Button>
                </DialogActions>
            </Dialog>
        )
    }

    const Loading = () => {
        return (
            <Backdrop
                sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open={open2}
                onClick={handleClose}
            >
                <CircularProgress color="inherit" />
            </Backdrop>
        )
    }

    return (
        <>
            {/* <Loading />
            <Dialogo1 />
            <Dialogo2 /> */}
            <TableRow
                key={row._id}
                hover
            >
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
                <TableCell
                    component="th"
                    id={labelId}
                    scope="row"
                    padding="none"
                >
                    {row.nome}
                </TableCell>
                <TableCell align="right">{row.data}</TableCell>
                <TableCell align="right">{row.hora}</TableCell>
                <TableCell align="right">{row.quantidade}</TableCell>
                <TableCell align="right">{row.situacao}</TableCell>
                <TableCell>
                    {row.situacao !== "SOLICITADO" ? null :
                        <Button
                            onClick={handleClickOpen}
                        >
                            enviar grt
                        </Button>}
                </TableCell>
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
            </TableRow>
        </>
    )
}
