import React from "react";
import { List, ListItemButton, ListItemText, Typography } from "@mui/material"
import style from './SolicitaServico.module.scss';
import { Box, display } from "@mui/system";

export const SolicitaServico = () => {

    const [selectedIndex, setSelectedIndex] = React.useState(1);

    const handleListItemClick = (event, index) => {
        setSelectedIndex(index);
    };

    return (
        <>
            <Typography variant='h6'>Selecione o servi√ßo desejado</Typography>
            <div className={style.opcoes}>
                <Box sx={{ width: '33%', bgcolor: 'background.paper', display: 'flex', flexDirection: "row"}}>
                    <nav aria-label="secondary mailbox folders">
                        <List component="nav" aria-label="secondary mailbox folder">
                            <ListItemButton
                                selected={selectedIndex === 2}
                                onClick={(event) => handleListItemClick(event, 2)}
                            >
                                <ListItemText primary="Trash" />
                            </ListItemButton>
                            <ListItemButton
                                selected={selectedIndex === 3}
                                onClick={(event) => handleListItemClick(event, 3)}
                            >
                                <ListItemText primary="Spam" />
                            </ListItemButton>
                        </List>
                    </nav>
                </Box>
            </div>
        </>
    )
}
