import React from "react";
import { Button, Checkbox, FormControl, FormControlLabel, Input, InputAdornment, Paper, TextField, Typography } from "@mui/material"
import style from './SolicitaServico.module.scss';
import { Box } from "@mui/system";
import moment from 'moment';

export const SolicitaServico = ({ user, veiculo, setCadastroServico }) => {

    console.log(user)
    console.log(veiculo)

    const [despachante, setDespachante] = React.useState({
        nome: user.nome,
        rg: user.rg,
        cpf: user.cpf,
        crdd: user.nCrddrs,
        logradouro: user.logradouro,
        cidade: user.cidade,
    })

    const [servico, setServico] = React.useState({
        inclusao_ou_alteracao_registro_financeiro: false,
        alteracao_info_proprietario: false,
        alteracao_info_veiculo: false,
        alteracao_restricao_financeira: false,
        alteracao_endereco_entrega_CRL_CRLV: false,
        correcao_restricao: false,
        emissao_DCPPO: false,
        fornecimento_placa_experiencia_fabricante: false,
        inclusao_restricao_financeira: false,
        liberacao_averbacao_execucao: false,
        liberacao_restricao_financeira: false,
        licenca_especial_transito: false,
        mudanca_placa_unica: false,
        primeiro_emplamaneto: false,
        renovacao_placa_experiencia_fabricante: false,
        reserva_placa: false,
        restricao_transferencia: false,
        solicitacao_autorizacao_alteracao_caracteristicas: false,
        solicitacao_autorizacao_regravacao_chassi: false,
        solicitacao_autorizacao_transporte_escolar: false,
    })

    const [novoEndereco, setNovoEndereco] = React.useState({
        novo_endereco: "",
        numero: "",
        bairro: "",
        cidade: "",
        uf: "",
        cep: ""
    })

    const [servico2, setServico2] = React.useState({
        averbacao_execucao: false,
        baixa_placa_experiencia_fabricante: false,
        baixa_outra_uf: false,
        baixa_veiculo: false,
        cancelamento_processo: false,
        colocacao_lacre_placa: false,
        comunicacao_venda: false,
        correcao_observacao_CRV_CRLV: false,
        correcao_chassi: false,
        correcao_info_proprietario: false,
        correcao_info_veiculo: false,
        correcao_municipio: false,
        autorizacao_detran: false,
        nao_autorizacao_detran: false,
        solicitacao_certidao: false,
        solicitacao_copia_documento: false,
        solicitacao_copia_CRLV: false,
        solicitacao_vistoria: false,
        solicitacao_DCPPO: false,
        transferencia_propriedade_veiculo_outro_uf: false,
        transferencia_propriedade_veiculo: false,
        troca_municipio_veiculo_outro_uf: false,
        troca_municipio_veiculo: false,
        outros: false,
        outros_entradas: ""
    })

    const [veic, setVeic] = React.useState({
        ao_veiculo: veiculo.modelo,
        chassi_ou_placa: veiculo.chassi
    })

    const [validaSeg, setValidaSeg] = React.useState({
        CRLV: false,
        CRV_CRLV: false,
        motivo: "",
    })

    const [ agenteFinanceiro, setAgenteFinanceiro ] = React.useState({
        agente: ''
    })

    const [ valmot, setValmot ] = React.useState(true)

    const segundaVia1 = (e) => {
        setValidaSeg(a => ({ ...a, CRLV: e.target.checked, CRV_CRLV: false }))
        if ((validaSeg.CRLV === false) && (validaSeg.CRV_CRLV === false)) {
            setValmot(false)
            setValidaSeg(a => ({...a, motivo: ""}))
        } else {
            setValmot(true)
        }
    }
    const segundaVia2 = (e) => {
        setValidaSeg(a => ({ ...a, CRV_CRLV: e.target.checked, CRLV: false }))
        if ((validaSeg.CRLV === false) && (validaSeg.CRV_CRLV === false)) {
            setValmot(false)
            setValidaSeg(a => ({...a, motivo: ""}))
        } else {
            setValmot(true)
        }
    }

    const [altEnd, setAltEnd] = React.useState(false)

    const [d, setD] = React.useState(moment().format('YYYY-MM-DD'))

    console.log('oooo::', d)

    console.log(moment(d).format('DD/MM/YYYY'))//Verifica aqui!!

    const alteraEndereco = (e) => {
        setServico(a => ({ ...a, alteracao_endereco_entrega_CRL_CRLV: e.target.checked }))
        setNovoEndereco(b => ({
            ...b,
            novo_endereco: "",
            numero: "",
            bairro: "",
            cidade: "",
            uf: "",
            cep: ""
        }))
    }
    const registroFinanceiro = (e) => {
        setServico(a => ({ ...a, inclusao_ou_alteracao_registro_financeiro: false }))
        setAgenteFinanceiro(a => ({ ...a, agente: '' }))
    }

    const Enviar = (e) => {
        e.preventDefault();
        console.log()
    }


    return (
        <>
            <form className={style.opcoes} onSubmit={Enviar}>
                <Box sx={{ width: '100%' }}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h5" align="center">GRT - GUIA DE RESPONSABILIDADE TÉCNICA</Typography>
                        <div className={style.boxlinha}>
                            <h2>Despachante / Auxiliar(DAV):</h2>
                            <FormControl sx={{ width: '40%' }} variant="standard" >
                                <Input value={despachante.nome}
                                    startAdornment={<InputAdornment position="start"></InputAdornment>}
                                    onChange={(e) => setDespachante(a => ({ ...a, nome: e.target.value }))}
                                />
                            </FormControl>
                            <h2>RG:</h2>
                            <FormControl sx={{ width: '20%' }} variant="standard" >
                                <Input value={despachante.rg}
