                                        <Input
                                            value={veic.chassi_ou_placa}
                                            onChange={(e) => setVeic(a => ({ ...a, chassi_ou_placa: e.target.value }))}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                            </div>
                        </div>
                        <div className={style.centrador}>
                            <input className={style.data} type='date' value={d} onChange={e => setD(e.target.value)} />
                            <div className={style.boxlinha}>
                                <Button sx={{ pt: 5 }} type='submit' >Salvar</Button>
                            </div>
                        </div>
                    </Paper>
                </Box>
            </form>
        </>
    )
}
