import React from 'react';
import '../../sass/Tela1/tela1.css'
import AACara from './AutorizaAlteraCaracteristica.jsx';
import ACLPlaca from './AutorizaColocaLacrePlaca';
import AFPlaca from './AutorizaFabPlaca';
import ARChassi from './AutorizaRegChassi';
import ARMotor from './AutorizaRegMotor';

const Tela2 = () => {

    const [tela1, setTela1] = React.useState(false);
    const [tela2, setTela2] = React.useState(false);
    const [tela3, setTela3] = React.useState(false);
    const [tela4, setTela4] = React.useState(false);
    const [tela5, setTela5] = React.useState(false);

    const botaoOpt1 = () => {
        tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
        !tela4 ? setTela4(tela4) : setTela4(!tela4);
        !tela5 ? setTela5(tela5) : setTela5(!tela5);
    }
    const botaoOpt2 = () => {
        !tela1 ? setTela1(tela1) : setTela1(!tela1);
        tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
        !tela4 ? setTela4(tela4) : setTela4(!tela4);
        !tela5 ? setTela5(tela5) : setTela5(!tela5);
    }
    const botaoOpt3 = () => {
        !tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        tela3 ? setTela3(tela3) : setTela3(!tela3);
        !tela4 ? setTela4(tela4) : setTela4(!tela4);
        !tela5 ? setTela5(tela5) : setTela5(!tela5);
    }
    const botaoOpt4 = () => {
        !tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
        tela4 ? setTela4(tela4) : setTela4(!tela4);
        !tela5 ? setTela5(tela5) : setTela5(!tela5);
    }
    const botaoOpt5 = () => {
        !tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
        !tela4 ? setTela4(tela4) : setTela4(!tela4);
        tela5 ? setTela5(tela5) : setTela5(!tela5);
    }

    return (
        <>
            <div className='tela--inicial--tela1'>
                <p className='item' onClick={botaoOpt1} >Autorização para Alteração de Características</p>
                <p className='item' onClick={botaoOpt2} >Autorização para Fabricação de Placa</p>
                <p className='item' onClick={botaoOpt3} >Autorização para Regravação de Chassi</p>
                <p className='item' onClick={botaoOpt4} >Autorização para Regravação de Motor</p>
                <p className='item' onClick={botaoOpt5} >Autorização para Colocação de Lacre em Placa</p>
            </div>
            {!tela1 ? null : <AACara />}
            {!tela2 ? null : <AFPlaca />}
            {!tela3 ? null : <ARChassi />}
            {!tela4 ? null : <ARMotor />}
            {!tela5 ? null : <ACLPlaca />}
        </>
    )
}

export default Tela2
